packagegui.custom_php_global_functions:

function sync_package_arpwatch() {
	global $config;
	$pf_version=substr(trim(file_get_contents("/etc/version")),0,3);
	conf_mount_rw();
	config_lock();

	if(isset($_POST['interface_array'])) {
		$int = $_POST['interface_array'];
	} else {
		$int = $config['installedpackages']['arpwatch']['config'][0]['interface_array'];
	}

	$mail = "";
	$debug = "";
	if(($pf_version > 2.0) && (isset($_POST['enable_email']) || ($config['installedpackages']['arpwatch']['config'][0]['enable_email'] == "on"))) {
		if (!empty($config['notifications']['smtp']['notifyemailaddress']))
			$mail = " -m \"{$config['notifications']['smtp']['notifyemailaddress']}\"";
	} else {
		$debug = "-d";
	}

	foreach($_POST['interface_array'] as $iface) {
		$int = convert_friendly_interface_to_real_interface_name($iface);
		if($int) {
			$log_file = "/var/log/arp.{$int}.dat";
			$start .= "touch {$log_file}\n";
			$start .="/usr/local/sbin/arpwatch {$debug} -f {$log_file} {$mail} -i {$int} > /var/log/arpwatch.reports.{$int} 2>&amp;1 &amp;\n";	
		}
	}

	$stop = "/usr/bin/killall arpwatch";
	write_rcfile(array(
		"file" => "arpwatch.sh",
		"start" => $start,
		"stop" =>  $stop)
	);

	restart_service("arpwatch");
	conf_mount_ro();
	config_unlock();
}
