packagegui.custom_php_global_functions:
	function sync_package_arpwatch() {
		global $config;
                conf_mount_rw();
                config_lock();
		$int = $config['installedpackages']['arpwatch']['config'][0]['interface'];
		$int = convert_friendly_interface_to_real_interface_name($int);
		$start = "/usr/local/sbin/arpwatch -d -i {$int} > /var/log/arpwatch.reports 2>&1 &";
		$stop = "/usr/bin/killall arpwatch";
		write_rcfile(array(
			"file" => "arpwatch.sh",
			"start" => $start,
			"stop" =>  $stop
			)
		);
		restart_service("arpwatch");
                conf_mount_ro();
                config_unlock();
        }
