packagegui.custom_php_install_command:

	global $config, $g;

	$pfs_version = substr(trim(file_get_contents("/etc/version")),0,3);
	switch ($pfs_version) {
		case "1.2":
		case "2.0":
			define('ZABBIX_PROXY_BASE', '/usr/local');
			break;
		case "2.1":
			define('ZABBIX_PROXY_BASE', '/usr/local');
			break;
		default:
			define('ZABBIX_PROXY_BASE', '/usr/pbi/zabbix2-proxy-' . php_uname("m"));
	}

	mwexec("mkdir -p /var/log/zabbix2/");
	mwexec("mkdir -p /var/run/zabbix2/");
	mwexec("mkdir -p /var/db/zabbix2/");

	conf_mount_rw();

	/* create a few directories and ensure the sample files are in place */
	exec("/bin/mkdir -p " . ZABBIX_PROXY_BASE . "/etc/zabbix2");
	exec("/bin/mkdir -p /var/log/zabbix2");
	exec("/bin/mkdir -p /var/run/zabbix2");
	exec("/bin/mkdir -p /var/db/zabbix2");

	exec("/bin/rm -f " . ZABBIX_PROXY_BASE . "/etc/rc.d/zabbix2_proxy");

	$start  = "/bin/mkdir -p /var/log/zabbix2\n";
	$start .= "/usr/sbin/chown -R zabbix:zabbix /var/log/zabbix2\n";

	$start .= "/bin/mkdir -p /var/run/zabbix2\n";
	$start .= "/usr/sbin/chown -R zabbix:zabbix /var/run/zabbix2\n";

	$start .= "/bin/mkdir -p /var/db/zabbix2\n";
	$start .= "/usr/sbin/chown -R zabbix:zabbix /var/db/zabbix2\n";

	$start .= "echo \"Starting Zabbix Proxy\"...\n";

	/* start zabbix proxy */
	$start .= ZABBIX_PROXY_BASE . "/sbin/zabbix_proxy\n";

	$stop  = "echo \"Stopping Zabbix Proxy\"\n";
   $stop .= "/usr/bin/killall zabbix_proxy\n";
	/* write out rc.d start/stop file */
	write_rcfile(array(
		"file" => "zabbix2_proxy.sh",
		"start" => "{$start}",
		"restart" => "$stop\n" . "sleep 5\n" . "{$start}",
		"stop" => "$stop"
		)
	);

	conf_mount_ro();

packagegui.custom_php_validation_command:

	global $_POST;

   $ServerPort=$_POST['serverport'];
   if (!preg_match("/^\d+$/", $ServerPort)) {
      $input_errors[]='Server Port is not numeric.';
   }

	$ConfigFrequency=$_POST['configfrequency'];
	if (!preg_match("/^\d+$/", $ConfigFrequency)) {
		$input_errors[]='Config Frequency is not numeric.';
	}

packagegui.custom_php_resync_config_command:

conf_mount_rw();
global $config, $g;

$pfs_version = substr(trim(file_get_contents("/etc/version")),0,3);
switch ($pfs_version) {
	case "1.2":
	case "2.0":
		define('ZABBIX_PROXY_BASE', '/usr/local');
		break;
	default:
		define('ZABBIX_PROXY_BASE', '/usr/pbi/zabbix2-proxy-' . php_uname("m"));
}

$zabbixproxy_config = $config['installedpackages']['zabbixproxy']['config'][0];

$Server=$zabbixproxy_config['server'];
$ServerPort=$zabbixproxy_config['serverport'];
$Hostname=$zabbixproxy_config['hostname'];
$ListenPort=$zabbixproxy_config['listenport'];
$ConfigFrequency=$zabbixproxy_config['configfrequency'];
if(isset($zabbixproxy_config['activemode'])) {
	$Mode="0"; /* active */
} else {
	$Mode="1"; /* passive */
}

$conf = "Server=$Server\n";
$conf .= "ServerPort=$ServerPort\n";
$conf .= "Hostname=$Hostname\n";
$conf .= "PidFile=/var/run/zabbix2/zabbix2_proxy.pid\n";
$conf .= "DBName=/var/db/zabbix2/proxy.db\n";
$conf .= "LogFile=/var/log/zabbix2/zabbix_proxy.log\n";
$conf .= "ConfigFrequency=$ConfigFrequency\n";
$conf .= "FpingLocation=/usr/local/sbin/fping\n";
/* there's currently no fping6 (IPv6) dependency in the package, but if there was, the binary would likely also be in /usr/local/sbin */
$conf .= "Fping6Location=/usr/local/sbin/fping6\n";
$conf .= "ProxyMode=$Mode\n";

file_put_contents(ZABBIX_PROXY_BASE . "/etc/zabbix2/zabbix_proxy.conf", $conf);

$want_sysctls = array(
	'kern.ipc.shmall' => '2097152',
	'kern.ipc.shmmax' => '2147483648',
	'kern.ipc.semmsl' => '250'
);
$sysctls = array();
if (file_exists("/etc/sysctl.conf")) {
	$sc = file_get_contents("/etc/sysctl.conf");
	$sc = explode("\n", $sc);
	foreach ($sc as $num => $line) {
		list($sysctl, $val) = explode("=", $line, 2);
		if (array_key_exists($sysctl, $want_sysctls) || empty($sysctl))
			unset($sc[$num]);
	}
}
foreach ($want_sysctls as $ws => $wv) {
	$sc[] = "{$ws}={$wv}";
	exec("/sbin/sysctl {$ws}={$wv}");
}
file_put_contents("/etc/sysctl.conf", implode("\n", $sc) . "\n");

$want_tunables = array(
	'kern.ipc.semopm' => '100',
	'kern.ipc.semmni' => '128',
	'kern.ipc.semmns' => '32000',
	'kern.ipc.shmmni' => '4096'
);
$tunables = array();
if (file_exists("/boot/loader.conf")) {
	$lt = file_get_contents("/boot/loader.conf");
	$lt = explode("\n", $lt);
	foreach ($lt as $num => $line) {
		list($tunable, $val) = explode("=", $line, 2);
		if (array_key_exists($tunable, $want_tunables) || empty($tunable))
			unset($lt[$num]);
	}
}
foreach ($want_tunables as $wt => $wv) {
	$lt[] = "{$wt}={$wv}";
}
file_put_contents("/boot/loader.conf", implode("\n", $lt) . "\n");
chmod("/var/log/zabbix2", 0755);
chmod("/var/run/zabbix2", 0755);
conf_mount_ro();

packagegui.custom_php_deinstall_command:

$pfs_version = substr(trim(file_get_contents("/etc/version")),0,3);
switch ($pfs_version) {
	case "1.2":
	case "2.0":
		define('ZABBIX_AGENT_BASE', '/usr/local');
		define('ZABBIX_PROXY_BASE', '/usr/local');
		break;
	default:
		define('ZABBIX_AGENT_BASE', '/usr/pbi/zabbix2-agent-' . php_uname("m"));
		define('ZABBIX_PROXY_BASE', '/usr/pbi/zabbix2-proxy-' . php_uname("m"));
}

exec("/usr/bin/killall zabbix_proxy");

exec("/bin/rm " . ZABBIX_PROXY_BASE . "/etc/rc.d/zabbix2_proxy.sh");

// Check if Zabbix-2 Agent exists
if(file_exists(ZABBIX_AGENT_BASE . "/etc/zabbix2/zabbix_agentd.conf")) {
	exec("/bin/rm  /var/log/zabbix2/zabbix_proxy.log");
	exec("/bin/rm  /var/run/zabbix2/zabbix2_proxy.pid");
} else {
	exec("/bin/rm -r /var/log/zabbix2/");
	exec("/bin/rm -r /var/run/zabbix2/");
}

exec("/bin/rm -r /var/db/zabbix2/");
